import {useState} from 'react'

function App() {
  const [categoriaAtual, setCategoriaAtual] = useState("btn-todas-categorias")
  console.log(categoriaAtual)
  return (
    <>
      <Header />
      <NovoFormCuriosidade />
      <main className="main">
        <ListaCategorias setCategoriaAtual={setCategoriaAtual} />
        <ListaCuriosidades />
      </main>
    </>
  );
}

function Header() {
  const tituloApp = "Hoje Aprendi!";
  return (
    <header className="header">
      <div className="logo">
        <img src="./logo.jpeg" height="68" width="68" alt="Logo Hoje Aprendi" />
        <h1>{tituloApp}</h1>
      </div>
      <button className="btn btn-grande">Compartilhe uma curiosidade</button>
    </header>
  );
}

const curiosidadesIniciais = [
  {
    id: 1,
    texto:
      "Lorem, ipsum dolor sit amet consectetur adipisicing elit. Nisi sint eaque laudantium.",
    fonte: "http://exemplo.com.br",
    categoria: "tecnologia",
    criadoEm: 2023,
    votoCurti: 0,
    votoMeImpressionei: 0,
    votoFalso: 0,
  },
  {
    id: 2,
    texto:
      "Lorem, ipsum dolor sit amet consectetur adipisicing elit. Nisi sint eaque laudantium.",
    fonte: "http://exemplo.com.br",
    categoria: "not√≠cias",
    criadoEm: 2023,
    votoCurti: 0,
    votoMeImpressionei: 0,
    votoFalso: 0,
  },
  {
    id: 3,
    texto:
      "Lorem, ipsum dolor sit amet consectetur adipisicing elit. Nisi sint eaque laudantium.",
    fonte: "http://exemplo.com.br",
    categoria: "hist√≥ria",
    criadoEm: 2023,
    votoCurti: 0,
    votoMeImpressionei: 0,
    votoFalso: 0,
  },
  {
    id: 4,
    texto:
      "Lorem, ipsum dolor sit amet consectetur adipisicing elit. Nisi sint eaque laudantium.",
    fonte: "http://exemplo.com.br",
    categoria: "finan√ßas",
    criadoEm: 2023,
    votoCurti: 0,
    votoMeImpressionei: 0,
    votoFalso: 0,
  },
  {
    id: 5,
    texto:
      "Lorem, ipsum dolor sit amet consectetur adipisicing elit. Nisi sint eaque laudantium.",
    fonte: "http://exemplo.com.br",
    categoria: "sociedade",
    criadoEm: 2023,
    votoCurti: 0,
    votoMeImpressionei: 0,
    votoFalso: 0,
  },
];

const CATEGORIAS = [
  { nome: "tecnologia", cor: "#3b82f6" },
  { nome: "ci√™ncia", cor: "#16a34a" },
  { nome: "finan√ßas", cor: "#ef4444" },
  { nome: "sociedade", cor: "#eab308" },
  { nome: "entretenimento", cor: "#db2777" },
  { nome: "sa√∫de", cor: "#14b8a6" },
  { nome: "hist√≥ria", cor: "#f97316" },
  { nome: "not√≠cias", cor: "#8b5cf6" },
];

//validador de Url 
function isValidHttpUrl(string) {
  let url;
  try {
    url = new URL(string);
  } catch (_) {
    return false;
  }
  return url.protocol === 'http:' || url.protocol === 'https:';
}

function NovoFormCuriosidade(){
  const [texto, setTexto] = useState('')
  const [fonte, setFonte] = useState('')
  const [categoria, setCategoria] = useState('')
  const tamanhoTexto = texto.length
  function controleForm(e){
    //previne recarregamento
    e.preventDefault()

    if(texto && isValidHttpUrl(fonte) && categoria && tamanhoTexto <= 200){
      const novaCuriosidade = {
        id : Math.round(Math.random() * 100000),
        texto,
        fonte,
      }
    }
  }
  return(
    <form className='curiosidade-form' onSubmit={controleForm}>
      <input type="text"
      placeholder='Compartilhe uma curiosidade...'
      value={texto}
      onChange={(e) => setTexto(e.target.value)}
       />
       <span>{200 - tamanhoTexto}</span>
       <input type="text"
       placeholder='Fonte confi√°vel' 
       value={fonte}
       onChange={(e) => setFonte(e.target.value)}/>
       <select value={categoria}
       onChange={(e) => setCategoria(e.target.value)}>
        <option value="">Escolha a categoria:</option>
        {CATEGORIAS.map((cat) =>(
          <option key={cat.nome} value={cat.nome}>{cat.nome.toLocaleUpperCase()}</option>
        ))}
       </select>
       <button className='btn btn-grande'>Postar</button>
    </form>
  )
}

function ListaCategorias({ setCategoriaAtual }) {
  return (
    <aside>
      <ul>
        <li className="categoria">
          <button className="btn btn-todas-categorias"
          onClick={() => setCategoriaAtual("todos") }>Todos</button>
        </li>

        {CATEGORIAS.map((cat) => (
          <li key={cat.nome} className="categoria">
            <button
              className="btn btn-categoria"
              style={{ backgroundColor: cat.cor }}
              onClick={() => setCategoriaAtual(cat.nome)}
            >
              {cat.nome}
            </button>
          </li>
        ))}
      </ul>
    </aside>
  );
}

function ListaCuriosidades() {
  return (
    <section>
      <ul className="lista-curiosidade">
        {curiosidadesIniciais.map((curiosidade) => (
          <li key={curiosidade.id} className="curiosidade">
            <p>
              {curiosidade.texto}
              <a
                className="fonte"
                href={curiosidade.fonte}
                target="_blank"
                rel="noreferrer"
              >
                (Fonte)
              </a>
            </p>
            <span
              className="tag-categoria"
              style={{
                backgroundColor: CATEGORIAS.find(
                  (cat) => cat.nome === curiosidade.categoria
                ).cor,
              }}
            >
              {curiosidade.categoria}
            </span>
            <div className="botao-voto">
              <button>‚ù§Ô∏è{curiosidade.votoCurti}</button>
              <button>ü§Ø{curiosidade.votoMeImpressionei}</button>
              <button>‚õîÔ∏è{curiosidade.votoFalso}</button>
            </div>
          </li>
        ))}
      </ul>
    </section>
  );
}

export default App;
